body {
  background-color: #130700;
  color: #d9d9d9;
  font-family: "Fondamento-Regular";
  width: 100%;
  height: 100%;
  overflow-x: hidden;
}

.header {
  margin-top: 25px;
  margin-bottom: 6rem;
  background-color: #130700;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
}
.header .logo {
  width: 100%;
  display: flex;
  justify-content: space-around;
  transition: all 1s ease-in;
}
.header .logo__image1 {
  margin: auto 0 auto 0;
  width: 20em;
  transform: rotateY(180deg);
  filter: invert(0.85);
}
.header .logo__image2 {
  margin: auto 0 auto 0;
  width: 20em;
  filter: invert(0.85);
}
.header .logo__title {
  margin: auto 0 auto 0;
  font-family: "Sherlock";
  font-size: 6.5rem;
}
.header .logo__menu {
  margin: auto;
  font-size: 35px;
}
.header .logo__menu__list {
  display: flex;
  justify-content: center;
}
.header .logo__menu__list__item {
  padding: 15px;
}
.header .logo__menu__list__item__link {
  text-decoration: none;
  color: #d9d9d9;
  font-family: "Fondamento-Regular";
  transition: all 0.4s ease-in-out;
  background-image: linear-gradient(#b29148, #130700);
  background-size: 0% 0.1em;
  background-position-y: 100%;
  background-position-x: 50%;
  background-repeat: no-repeat;
  transition: all 0.4s ease-in;
  text-decoration: none;
}
.header .logo__menu__list__item__link:hover {
  background-size: 100% 0.1em;
  color: #b29148;
}
.header .logo:hover {
  filter: invert(56%) sepia(49%) saturate(465%) hue-rotate(3deg) brightness(96%) contrast(84%);
}

.banner {
  width: 75%;
  margin: auto;
  display: flex;
  justify-content: center;
}
.banner__title {
  margin: 35px;
  font-size: 50px;
  font-family: "Fondamento-Regular";
  text-transform: uppercase;
}

.gallery {
  width: 85%;
  margin: auto;
}
